import requests 
from bs4 import BeautifulSoup
  
# api-endpoint 
URL = "https://servicios.igssgt.org/Servicios/Afiliados/ConsultaContribuciones.aspx"
  
# defining a params dict for the parameters to be sent to the API 
PARAMS = {
            '__EVENTTARGET':'ctl00$content$gridPatronos',
            '__EVENTARGUMENT':'Detalle$0',
            '__VIEWSTATE':'',
            '__VIEWSTATEGENERATOR':'1AA4A008',
            '__EVENTVALIDATION':'',
            'ctl00$content$txtNumAfiliado':'22710239801011',
            'ctl00$content$ddlDocumentos':'DPI',
            'ctl00$content$ddlOrdenCedula':'A01',
            'ctl00$content$txtDocumento':'22710239801011',
            'ctl00$content$btnConsultar':'Consultar'
        } 
  
# sending get request and saving the response as response object
r = requests.post(url = URL, params = PARAMS) 
  
# extracting data in json format 
# data = r.text() 

# print(data)

soup = BeautifulSoup(r.text, 'html.parser')
print(soup.prettify())